public class Esercizio4CicloFor {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		
		//abbiamo diviso in 3 blocchi 
		for(int i=0 ; i <=10;i++) {
			System.out.println(2*i);
		}
		int index=0;
		while(index<=10) {
			System.out.println(2+index);
			index++;
		}

	}

}
